#include <iostream>

using namespace std;

int main()
{
    int a, b, c;
    cout << "\nEnter A :";
    cin >> a;
    cout << "\nEnter B :";
    cin >> b;
    cout << "\nEnter C :";
    cin >> c;

    cout << "\nThis is multiple :" << a * b * c << endl;
    cout << "\nThis is sum :" << a - b << endl;
    cout << "\nThis is sub :" << a - b << endl;

    return 0;
}